dt-example.js:
$('#example').DataTable({
	ajax: '../data/2500.txt',
	deferRender: true,
	fixedHeader: {
		header: true,
		footer: true
	},
	scrollCollapse: true,
	scrollY: 200,
	scroller: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: '../data/2500.txt',
	deferRender: true,
	fixedHeader: {
		header: true,
		footer: true
	},
	scrollCollapse: true,
	scrollY: 200,
	scroller: true
});
