dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		fixedHeader: true
	} );

	var visible = true;
	var tableContainer = $(table.table().container());

	$('#toggle').on( 'click', function () {
		tableContainer.css( 'display', visible ? 'none' : 'block' );
		table.fixedHeader.adjust();

		visible = ! visible;
	} );
} );
